    void dragon_animation_phase(void) {
        if (get_current_wpm() <= DRAGON_IDLE_SPEED) {
            oled_write_raw_P(dragon[0], DRAGON_ANIM_SIZE);
        }
        if (get_current_wpm() > DRAGON_IDLE_SPEED && get_current_wpm() < TRANSITION_SPEED) {
            current_d2t_frame = (current_d2t_frame + 1) % D2T_FRAMES;
            oled_write_raw_P(d2t[abs((D2T_FRAMES - 1) - current_d2t_frame)], DRAGON_ANIM_SIZE);
        }
        if (get_current_wpm() >= TRANSITION_SPEED) {
            oled_write_raw_P(text[0], DRAGON_ANIM_SIZE);
        }
    }

    if (get_current_wpm() != 000) {
        oled_on();  // not essential but turns on animation OLED with any alpha keypress
        if (timer_elapsed32(dragon_timer) > DRAGON_ANIM_FRAME_DURATION) {
            dragon_timer = timer_read32();
            dragon_animation_phase();
        }
        dragon_sleep = timer_read32();
    }
    else {
        if (timer_elapsed32(dragon_sleep) > OLED_TIMEOUT) {
            oled_off();
        }
        else {
            if (timer_elapsed32(dragon_timer) > DRAGON_ANIM_FRAME_DURATION) {
                dragon_timer = timer_read32();
                dragon_animation_phase();
            }
        }
    }
